mapper.select:
select * from
(SELECT ROWNUM RN, t.* FROM
(SELECT restaurants.*,
CASE WHEN stores.stars IS NULL THEN 0 ELSE stores.stars / cnt END AS stars,
CASE WHEN stores.cnt IS NULL THEN 0 ELSE stores.cnt END AS cnt
FROM restaurants
LEFT JOIN stores ON restaurants.id = stores.id
where name like #{search}
ORDER BY stars desc)t
) WHERE RN BETWEEN #{a} AND #{b}

select * from
(SELECT ROWNUM RN, t.* FROM
(SELECT restaurants.*,
CASE WHEN stores.stars IS NULL THEN 0 ELSE stores.stars / cnt END AS stars,
CASE WHEN stores.cnt IS NULL THEN 0 ELSE stores.cnt END AS cnt
FROM restaurants
LEFT JOIN stores ON restaurants.id = stores.id
where cate like #{pick}
ORDER BY stars desc)t
) WHERE RN BETWEEN #{a} AND #{b}
